<style>
    div {
        container: text-banner / inline-size;
        display: flex;
        flex-wrap: wrap;
        justify-content: center;
        height: 100%;
        padding: 0 1em;
        pointer-events: none;
        position: absolute;
        width: 100%;
        z-index: -5;
    }

    svg {
        filter: drop-shadow(3px 3px #A25BFF) sepia(100%) drop-shadow(-3px -3px #BDF574);
    }

    svg:nth-of-type(1) { width: 100%; height: 33%;}
    svg:nth-of-type(2) { width: 100%; height: 33%;}
    svg:nth-of-type(3) { width: 100%; height: 33%;}

    @container text-banner (min-width: 700px) {
        svg:nth-of-type(1) { width: 70%; height: 40%; }
        svg:nth-of-type(2) { width: 30%; height: 20%; margin: 9.2em 0 0 0; }
        svg:nth-of-type(3) { width: 100%; height: 40%; }
    }
    
</style>
        